mapper.select:
select * from(select a .*,rownum rn from 
	(select color,pro_name,pro_tagprice,pro_number from product order by registertime desc) a
	where rownum <=${pageSize*pageNo} ) b where rn>${pageSize*(pageNo-1)}

select * from(select a .*,rownum rn from 
	(select color,pro_name,pro_tagprice,pro_number from product order by pro_tagprice desc) a
	where rownum <=${pageSize*pageNo} ) b where rn>${pageSize*(pageNo-1)}

select * from(select a .*,rownum rn from 
	(select color,pro_name,pro_tagprice,pro_number from product where pro_name like '%${searchName}%') a
	where rownum <=${pageSize*pageNo} ) b where rn>${pageSize*(pageNo-1)}

select * from(select a .*,rownum rn from 
	(select color,pro_name,pro_tagprice,pro_number from product where pro_tagprice between ${lowPrice} and ${heighPrice}) a
	where rownum <=${pageNo*pageSize} ) b where rn>${(pageNo-1)*pageSize}

select * from(select a .*,rownum rn from 
			(select color,pro_name,pro_tagprice,pro_number from product  order by  pro_number desc) a
		where rownum <=${pageSize*pageNo} ) b where rn>${pageSize*(pageNo-1)}
